package com.example.bysj2020.alipay;import android.text.TextUtils;public class PayResult {	private String resultStatus;	public PayResult(String rawResult) {		if (TextUtils.isEmpty(rawResult)) return;		String[] resultParams = rawResult.split(";");		for (String resultParam : resultParams) {			if (resultParam.startsWith("resultStatus")) {				resultStatus = gatValue(resultParam, "resultStatus");			}		}	}	@Override	public String toString() {		return "PayResult{" + "resultStatus='" + resultStatus + '\'' + '}';	}	private String gatValue(String content, String key) {		String prefix = key + "={";		return content.substring(content.indexOf(prefix) + prefix.length(),content.lastIndexOf("}"));	}	public String getResultStatus() {		return resultStatus;	}}